import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class OfertaProductoService {
  /*BehaviorSubject: Es un tipo de observable que tiene un valor inicial y emite el último valor emitido más el valor actual a todos sus suscriptores cuando se suscriben.
  //esta línea de código está creando un mecanismo para compartir y observar un valor específico (en este caso, un producto seleccionado) entre diferentes partes de tu aplicación.
  private productoSource = new BehaviorSubject<any>(null);
  producto$ = this.productoSource.asObservable();
  // esta línea de código se encarga de notificar a todos los componentes que están suscritos al BehaviorSubject que ha ocurrido un cambio en el producto seleccionado. 
  enviarProducto(producto: any) {
    this.productoSource.next(producto);
  }
  /*
  this.productoSource: Se refiere al BehaviorSubject que mencionamos anteriormente (productoSeleccionadoSource). Es la fuente que emite los cambios a los suscriptores.

.next(producto): Este método se utiliza para enviar un nuevo valor a los suscriptores del BehaviorSubject. En este caso, se está enviando el objeto producto como el nuevo valor.
  */
}
