import { Component } from '@angular/core';
import { ProductosService } from '../compPrincipal/producto/productos.service'; 
import { Producto } from '../compPrincipal/interfaces/Producto';
import { EditarProductoModalService } from '../editar-producto-modal.service';

@Component({
  selector: 'app-lista-productos',
  templateUrl: './lista-productos.component.html',
  styleUrls: ['./lista-productos.component.css']
})
export class ListaProductosComponent {
  productos: Producto[] = [];
  mostrarModal: boolean = false; //visibilidad del modal

  constructor(private productoService: ProductosService, private modalService: EditarProductoModalService) {

    this.productos = productoService.getListaProductos();

  }

  //apenas se renderice el componente tenemos que escucuchar al objeto observable (obtener el valor guardado en $modal)
  ngOnInit(){
    this.modalService.$modal.subscribe((valor)=> {
      this.modalSwitch = valor;
    })
  }

  modalSwitch(){
    this.mostrarModal = true;
  }

}
