import { Injectable } from '@angular/core';
import { ProductoComponent } from './producto.component';
import { Producto } from '../interfaces/Producto';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ProductosService {

  productoComponent: ProductoComponent [] = [];
  ultimoId: number=0;
  private productos: Producto[] = [];

  private apiUrl = 'http://localhost:8080/producto/lista';

  constructor(private http: HttpClient) { }


  getProductos(): Observable<Producto[]> {
    return this.http.get<Producto[]>(this.apiUrl).pipe(
      tap(productos => this.productos = productos)
    );
  }


  agregarProducto(producto: Producto): void {
    this.productos.push(producto);
  }

  editarProducto(id: number, producto: Producto): Observable<Producto> {
    const url = `http://localhost:8080/producto/editar/${id}`;
    return this.http.put<Producto>(url, producto).pipe(
      tap(productoEditado => {
        // Actualizar la lista local después de la edición
        const index = this.productos.findIndex(p => p.id === id);
        if (index !== -1) {
          this.productos[index] = productoEditado;
        }
      })
    );
    }
}


