import { Component } from '@angular/core';
import { ProductosService } from '../compPrincipal/producto/productos.service'; 
import { Producto } from '../compPrincipal/interfaces/Producto';

@Component({
  selector: 'app-lista-productos',
  templateUrl: './lista-productos.component.html',
  styleUrls: ['./lista-productos.component.css']
})
export class ListaProductosComponent {
  productos: Producto[] = [];
  productoSeleccionado: Producto | null = null;

  constructor(private productoService: ProductosService, private lista) {

    this.productos = productoService.getListaProductos();

  }

  editarProducto(producto: Producto): void {
    // Guarda el producto seleccionado para editarlo
    this.productoSeleccionado = { ...producto };
    console.log(this.productoSeleccionado);
  }

  guardarProducto(): void {
    const updatedProducts = this.productos.map(product => {
      return product.id === this.productoSeleccionado?.id ? { ...this.productoSeleccionado } : product;
    });
    //console.log(updatedProducts);
    this.productos = [...updatedProducts];
    this.productoSeleccionado = null;
  }
}

/*
this.productos.map(product => {...}): This uses the map function to create a new array by iterating over each element (product) in the existing this.productos array.
product.id === this.productoSeleccionado?.id ? { ...this.productoSeleccionado } : product: For each product, it checks if the id of the current product matches the id of this.productoSeleccionado.
If the IDs match, it returns a new object { ...this.productoSeleccionado }, which is a shallow copy of the this.productoSeleccionado object. This ensures that the original array remains immutable, and only the selected product is replaced with the updated one.
If the IDs do not match, it returns the original product unchanged.
this.productos = [...updatedProducts]: This line updates the original this.productos array with the new array (updatedProducts). 
By assigning this new array to this.productos, you effectively update the original array with the modified product.
this.productoSeleccionado = null;: Finally, this line sets this.productoSeleccionado to null, indicating that there is no longer a selected product. 
*/
