import { Component } from '@angular/core';
import { ProductosService } from '../compPrincipal/producto/productos.service'; 
import { Producto } from '../compPrincipal/interfaces/Producto';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';




@Component({
  selector: 'app-lista-productos',
  templateUrl: './lista-productos.component.html',
  styleUrls: ['./lista-productos.component.css']
})
export class ListaProductosComponent {
  productos: Producto[] = [];
  productoSeleccionado: Producto | null = null;
  productoElegido: Producto | null = null;

  constructor(private productoService: ProductosService, private modalService: NgbModal) {}

  ngOnInit() {
    //consigue la lista de productos para mostrar en la tabla
    this.productoService.getProductos().subscribe((productos: Producto[]) => {
      console.log("Productos: ", productos);
      this.productos = productos;
    });
  }

  editarProducto(id: number): void {
    console.log(`Editar producto con ID: ${id}`);
    const productoElegido = this.productos.find(p => p.id === id);

    if (productoElegido) {
      this.productoElegido = productoElegido;
      console.log(this.productoElegido);
    } else {
      console.error(`No se encontró un producto con ID ${id}`);
    }
  }

  guardar(): void {
    if (this.productoElegido) {
      this.productoService.actualizar(this.productoElegido).subscribe({
        next: (response) => {
          console.log('Respuesta del servidor:', response);
          // Recargar la lista de productos
          this.cargarListaProductos();
          // Cerrar el modal después de guardar
          this.modalService.dismissAll();
        },
        error: (error) => {
          console.error('Error al actualizar el producto:', error);
        }
      });
    }
  }

  cargarListaProductos() {
    this.productoService.getProductos().subscribe((productos: Producto[]) => {
      this.productos = productos;
    });
  }
}
