import { Component } from '@angular/core';
import { ProductosService } from '../compPrincipal/producto/productos.service'; 
import { Producto } from '../compPrincipal/interfaces/Producto';
import { ListaProductoService } from '../lista-producto.service';
import { NgForm } from '@angular/forms';


@Component({
  selector: 'app-lista-productos',
  templateUrl: './lista-productos.component.html',
  styleUrls: ['./lista-productos.component.css']
})
export class ListaProductosComponent {
  productos: Producto[] = [];
  productoSeleccionado: Producto | null = null;
  nombreProducto: string = ''; //para editar
  descripcionProducto: string = ''
  constructor(private productoService: ProductosService, private formProductoService: ProductosService) {}

  ngOnInit() {

    //consigue la lista de productos para mostrar en la tabla
    this.productoService.getProductos().subscribe((productos: Producto[]) => {
      console.log("Productos: ", productos);
      this.productos = productos;
    });
  }




  editarProducto(id: number): void {
    console.log(`Editar producto con ID: ${id}`);
    let productoElegido = this.productos[id];
    console.log(productoElegido);
    console.log(productoElegido)
  }

  guardarProducto(): void {

  }

}
