import { Component, OnInit } from '@angular/core';
import { Oferta } from '../compPrincipal/interfaces/oferta';
import { ListaOfertasService } from '../lista-ofertas.service';
import { OfertaService } from '../oferta.service';
import { OfertaProductoService } from '../oferta-producto.service';

@Component({
  selector: 'app-lista-ofertas',
  templateUrl: './lista-ofertas.component.html',
  styleUrls: ['./lista-ofertas.component.css']
})
export class ListaOfertasComponent implements OnInit{
  ofertas: Oferta[] = [];
  ofertaSeleccionada: Oferta | null = null;
  producto: any;

  constructor(private ofertaService: OfertaService, private listaOfertasService: ListaOfertasService, private ofertaProductoService: OfertaProductoService) {

    this.ofertas = ofertaService.getListaOfertas();

  }

  ngOnInit() {
    this.ofertaProductoService.producto$.subscribe((producto) => {
      this.producto = producto;
    });
  }

  // Expose the service methods to the template
  editarOferta(oferta: Oferta): void {
    this.listaOfertasService.editarOferta(oferta);
    this.ofertaSeleccionada = { ...oferta }; // Optionally set the selected offer in the component
  }

    // Call the guardarOferta method without passing any arguments
    guardarOferta(): void {
      if (this.ofertaSeleccionada) {
        // Save the edited product using the service method
        this.listaOfertasService.guardarOferta();

        // Optionally, update the local products list
        const updatedOffers = this.ofertas.map(oferta => {
          return oferta.id === this.ofertaSeleccionada?.id ? { ...this.ofertaSeleccionada } : oferta;
        });
        this.ofertas = [...updatedOffers];

        // Reset the selected offer after saving changes
        this.ofertaSeleccionada = null;
      }
    }
}

<div class="container">
    <div class="container d-flex justify-content-between mb-4">
      <h1>Lista de  Ofertas</h1>
      <button type="button" class="btn btn-dark" data-bs-toggle="modal" data-bs-target="#staticBackdrop2">Agregar</button>
      <app-form-oferta></app-form-oferta>
    </div>
    <table class="table table-striped table-hover mt-4 text-center align-middle">
      <thead>
        <tr class="text-center ">
          <th>Codigo</th>
          <th>Stock</th>
          <th>Descripcion</th>
          <th>Precio</th>
          <th>Vigencia</th>
          <th>Producto</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        <a [routerLink]="['/detalles-producto', producto.id]" [fragment]="'producto-' + producto.id">
          {{ producto.nombre }}
        </a>
      </tbody>
    </table>

  </div>


  <!-- Modal -->
  <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog">
      <div class="modal-content">

        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">Editar oferta</h5>
          <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>

        <div  class="modal-body" >
          <form #productoForm="ngForm" class="row g-3">

            <div class="col-6">
              <label for="codigoInput" class="form-label">Codigo</label>
              <input 
                name="codigo" 
                type="text" 
                class="form-control" 
                id="codigoInput" 
                placeholder="ingrese el codigo" 
                [(ngModel)]="ofertaSeleccionada && ofertaSeleccionada.codigo">
            </div>

            <div class="col-6">
              <label for="nombreInput" class="form-label">Stock</label>
              <input 
                name="stock" 
                type="text" 
                class="form-control" 
                id="stockInput" 
                placeholder="ingrese el stock" 
                [(ngModel)]="ofertaSeleccionada && ofertaSeleccionada.stock">
            </div>

            <div class="col-6">
              <label for="descripcionInput" class="form-label">Descripcion</label>
              <textarea 
                name="descripcion" 
                class="form-control" 
                id="descripcion" 
                placeholder="ingrese la descripcion" 
                [(ngModel)]="ofertaSeleccionada && ofertaSeleccionada.descripcion"></textarea>
            </div>

            <div class="col-6">
                <label for="precioInput" class="form-label">Precio</label>
                <input 
                  name="precio" 
                  type="text" 
                  class="form-control" 
                  id="precioInput" 
                  placeholder="ingrese el precio" 
                  [(ngModel)]="ofertaSeleccionada && ofertaSeleccionada.precio">
              </div>

              <div class="col-6">
                <label for="vigenciaInput" class="form-label">Vigencia</label>
                <input 
                  name="vigencia" 
                  type="text" 
                  class="form-control" 
                  id="vigenciaInput" 
                  placeholder="ingrese la vigencia" 
                  [(ngModel)]="ofertaSeleccionada && ofertaSeleccionada.vigencia">
              </div>


          </form>
        </div>

        <div class="modal-footer">
          <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancelar</button>
          <button type="button" class="btn btn-dark" (click)="guardarOferta()">Guardar</button>
          <!--
            boton real
            <button type="Submit" form="frmOferta" class="btn btn-primary">Guardar</button>
          -->
        </div>

      </div>
    </div>
  </div>
