import { Component } from '@angular/core';
import { ProductosService } from '../compPrincipal/producto/productos.service'; 
import { Producto } from '../compPrincipal/interfaces/Producto';
import { ListaProductoService } from '../lista-producto.service';


@Component({
  selector: 'app-lista-productos',
  templateUrl: './lista-productos.component.html',
  styleUrls: ['./lista-productos.component.css']
})
export class ListaProductosComponent {
  productos: Producto[] = [];
  productoSeleccionado: Producto | null = null;

  constructor(private productoService: ProductosService, private listaProductosService: ListaProductoService) {

    //this.productos = productoService.getListaProductos();

  }

  ngOnInit() {
    this.productoService.getProductos().subscribe((productos: Producto[]) => {
      console.log(productos); 
      this.productos = productos;
    });
  }

  // Expose the service methods to the template
  editarProducto(producto: Producto): void {
    this.listaProductosService.editarProducto(producto);
    this.productoSeleccionado = { ...producto }; // Optionally set the selected product in the component
  }

    // Call the guardarProducto method without passing any arguments
    guardarProducto(): void {
      if (this.productoSeleccionado) {
        // Save the edited product using the service method
        this.listaProductosService.guardarProducto();

        // Optionally, update the local products list
        const updatedProducts = this.productos.map(product => {
          return product.id === this.productoSeleccionado?.id ? { ...this.productoSeleccionado } : product;
        });
        this.productos = [...updatedProducts];

        // Reset the selected product after saving changes
        this.productoSeleccionado = null;
      }
    }

}
