import { Component } from '@angular/core';
import { ProductosService } from '../compPrincipal/producto/productos.service'; 
import { Producto } from '../compPrincipal/interfaces/Producto';

@Component({
  selector: 'app-lista-productos',
  templateUrl: './lista-productos.component.html',
  styleUrls: ['./lista-productos.component.css']
})
export class ListaProductosComponent {
  productos: Producto[] = [];
  enEdicion: boolean = false;
  productoEnEdicion: Producto = {} as Producto;
  mostrarModal: boolean = false; //visibilidad del modal

  constructor(private productoService: ProductosService) {
    this.productos = productoService.getListaProductos();
  }

  editarProducto(producto: Producto): void {
    this.enEdicion = true;
    this.productoEnEdicion = { ...producto };
    this.mostrarModal = true; // Abrir el modal
  }

  guardarCambiosEdicion(productoEditado: Producto): void {
    this.productoService.actualizarProducto(productoEditado);
    this.cancelarEdicion();
  }

  cancelarEdicion(): void {
    this.enEdicion = false;
    this.productoEnEdicion = {} as Producto;
    this.mostrarModal = false; // Cerrar el modal
  }
}

<div class="container">
  <div class="container d-flex justify-content-between mb-4">
    <h1>Lista de  productos</h1>
    <button type="button" class="btn btn-dark">Agregar</button>
  </div>
  <table class="table table-striped table-hover mt-4 text-center align-middle">
    <thead>
      <tr class="text-center ">
        <th>Codigo</th>
        <th>Nombre</th>
        <th>Descripcion</th>
        <th>Imagen</th>
        <th>Acciones</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let producto of productos">
        <td>{{ producto.codigo }}</td>
        <td>{{ producto.nombre }}</td>
        <td>{{ producto.descripcion }}</td>
        <td>{{ producto.imagenProducto }}</td>
        <td>
          <div class="d-flex justify-content-end">

            <app-editar-producto-modal *ngIf="mostrarModal && enEdicion && productoEnEdicion?.id === producto.id"
          [productoEnEdicion]="productoEnEdicion"
          (guardarCambios)="guardarCambiosEdicion($event)"
          (cancelarEdicion)="cancelarEdicion()">
            </app-editar-producto-modal>

            <button (click)="editarProducto(producto)" type="button" class="btn btn-outline-dark m-2">
              <i class="bi bi-pencil-square"></i>
            </button>
            <button type="button" class="btn btn-outline-dark m-2">
              <i class="bi bi-trash"></i>
            </button>
          </div>
        </td>
      </tr>
    </tbody>
  </table>

</div>
