import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Producto } from '../compPrincipal/interfaces/Producto';

@Component({
  selector: 'app-editar-producto-modal',
  templateUrl: './editar-producto-modal.component.html',
  styleUrls: ['./editar-producto-modal.component.css']
})
export class EditarProductoModalComponent {
  @Input()productoEnEdicion!: Producto;
  @Output() guardarCambios = new EventEmitter<Producto>();
  @Output() cancelarEdicion = new EventEmitter<void>();
}
<div class="modal">
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title">Editar Producto</h5>
          <button type="button" class="close" (click)="cancelarEdicion()">
            <span>&times;</span>
          </button>
        </div>

        <div class="modal-body">
          <!-- Campos de edición -->
          <label for="nombre">Nombre:</label>
          <input id="nombre" [(ngModel)]="productoEnEdicion.nombre" />

          <label for="descripcion">Descripción:</label>
          <input id="descripcion" [(ngModel)]="productoEnEdicion.descripcion" />
          <!-- ... otros campos de edición -->
        </div>

        <div class="modal-footer">
          <button type="button" class="btn btn-primary" (click)="guardarCambios.emit(producto)">Guardar</button>
          <button type="button" class="btn btn-secondary" (click)="cancelarEdicion()">Cancelar</button>
        </div>
      </div>
    </div>
  </div>
