import { Component } from '@angular/core';
import { Producto } from '../compPrincipal/interfaces/Producto';
import { ProductoComponent } from '../compPrincipal/producto/producto.component';

@Component({
  selector: 'app-formulario-productos',
  templateUrl: './formulario-productos.component.html',
  styleUrls: ['./formulario-productos.component.css']
})
export class FormularioProductosComponent {
  //Modelo para el formulario
  modeloFormulario: Producto = {
    id: 0,
    codigo: 0,
    nombre: '',
    descripcion: '',
    imagenProducto: ''
  };

  // Función para manejar la presentación del formulario
  onSubmit(formulario: NgForm) {
    // Encuentra el producto por su ID en la lista
    const productoAModificar = this.listaProductos.find(p => p.id === this.modeloFormulario.id);

    if (productoAModificar) {
      // Actualiza los valores del producto con los del formulario
      productoAModificar.nombre = this.modeloFormulario.nombre;
      productoAModificar.precio = this.modeloFormulario.precio;

      // Luego puedes hacer lo que necesites con estos valores, por ejemplo, enviarlos al servidor.
    }
  }

}
