import { Injectable } from '@angular/core';
import { Producto } from './compPrincipal/interfaces/Producto';
import { HttpClient } from '@angular/common/http';
import { Observable, catchError, tap } from 'rxjs';
import { ProductosService } from './compPrincipal/producto/productos.service';


@Injectable({
  providedIn: 'root'
})
export class ListaProductoService {

  private productos: Producto[] = [];
  productoSeleccionado: Producto | null = null;

  private apiUrl = 'http://localhost:8080/producto';

  constructor(private http: HttpClient, private productoService: ProductosService) { }

  getListaProductos(): Producto[] {
    return this.productos;
  }

  editarNombre(id: number, nuevoNombre: string) {
    const url = `${this.apiUrl}/editar/${id}?nombre=${encodeURIComponent(nuevoNombre)}`;
    return this.http.put(url, {});
  }

  editarDescripcion(id: number, nuevaDescripcion: string) {
    const url = `${this.apiUrl}/editar/${id}?descripcion=${encodeURIComponent(nuevaDescripcion)}`;
    return this.http.put(url, {});
  }

  editarImagen(id: number, nuevaImagen: string) {
    const url = `${this.apiUrl}/editar/${id}?imagenProducto=${encodeURIComponent(nuevaImagen)}`;
    return this.http.put(url, {});
  }

}
